INSERT INTO `haisanviethung`.`products` (`id`, `description`, `detail`, `nameProduct`, `price`, `quantity`, `idCategory`) VALUES ('5', '<p>Trong các dòng cá, <strong>cá chình</strong> có nhiều đạm, nổi tiếng về sự bổ dưỡng,&nbsp;thịt béo thơm và lành, có tác dụng tráng dương bổ thận, rượu pha mật cá chình có tác dụng chữa bệnh đường ruột. Vị ngon bổ có thể sánh với yến sào, gân hươu.<br>
