INSERT INTO `haisanviethung`.`products` (`id`, `description`, `detail`, `nameProduct`, `price`, `quantity`, `idCategory`) VALUES ('27', '<p><strong>Ốc móng tay</strong> là loài nhuyễn thể thường sống trong cát bùn ở các cửa biển, hình dạng rất giống ngón tay nên nó được mang cái tên như thế. Thức ăn chủ yếu của loại ốc này là cá sinh vật phù du trên biển, vì vậy bạn có thể yên tâm khi thưởng thức mà không phải lo sợ chúng được nuôi theo kiểu công nghiệp.<br>
