INSERT INTO `haisanviethung`.`products` (`id`, `description`, `detail`, `nameProduct`, `price`, `quantity`, `idCategory`) VALUES ('33', '<p><strong>Sò mai</strong> có hai mảnh vỏ giống hình tam giác úp lại, bên trong là phần thịt sò. Người ta dùng phần thịt của con sò để chế biến thành nhiều món ăn, món nào cũng ngon và lạ. Sò mai biển giá trị nhất ở 2 cái cồi, dân địa phương gọi là “điệp nữ”, “ngọc nữ”.<br>
