INSERT INTO `haisanviethung`.`products` (`id`, `description`, `detail`, `nameProduct`, `price`, `quantity`, `idCategory`) VALUES ('34', '<p>Việt Hưng chuyên cung cấp <strong>sò méo</strong>&nbsp;còn sống 100% tận nơi, sạch cát, giao hàng siêu nhanh.<br>
