INSERT INTO `haisanviethung`.`products` (`id`, `description`, `detail`, `nameProduct`, `price`, `quantity`, `idCategory`) VALUES ('35', '<p><strong>Ngao sần sống</strong>&nbsp;về hàng mới liên tục mỗi ngày tại shop, một trong những loại ốc phổ biến được rất nhiều KH yêu thích và lựa chọn. Thịt ngao sần ngọt thịt và giòn dai tự nhiên.</p>
