INSERT INTO `haisanviethung`.`products` (`id`, `description`, `detail`, `nameProduct`, `price`, `quantity`, `idCategory`) VALUES ('36', '<p><strong>Sò thưng</strong> là món ăn chứa nhiều chất dinh dưỡng cần thiết cho cơ thể như hàm lượng đạm, magie, kẽm, omega-3 cao,… mang lại nhiều lợi ích tuyệt vời cho sức khỏe của bạn.<br>
♥ Món ngon: Hấp, nướng mỡ hành, nướng bơ,…<br>
♥ Giao sống nội thành trong 2h</p>', '<p><strong>&nbsp;</strong></p>
<p><strong>ĐẶC ĐIỂM NỔI BẬT:</strong></p>
